import React from 'react';
import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [attractions, setAttraction] = useState([]);

  // Note: the empty deps array [] means
  // this useEffect will run once
  // similar to componentDidMount()
  useEffect(() => {
    fetch("http://localhost:3333/attractions")
      .then(res => res.json())
      .then(
        (result) => {
          setItems(result);
        },
      )
  }, [])
  return (
    <div>
      <Container>
        <Row>
          <Col xs={12} sm={6}>
            <Card style={{ width: '100%' }}>
              <Card.Img variant="top" src="https://www.melivecode.com/attractions/1.jpg" />
              <Card.Body>
                <Card.Title>"Phi Phi Islands"</Card.Title>
                <Card.Text>
                Phi Phi Islands are a group of islands in Thailand between the large island of Phuket and the Malacca Coastal Strait of Thailand.
                </Card.Text>
                <Button variant="primary">Read more</Button>
              </Card.Body>
            </Card>
          </Col>
          <Col xs={12} sm={6}>
            <Card style={{ width: '100%' }}>
              <Card.Img variant="top" src="https://www.melivecode.com/attractions/1.jpg" />
              <Card.Body>
                <Card.Title>"Phi Phi Islands"</Card.Title>
                <Card.Text>
                Phi Phi Islands are a group of islands in Thailand between the large island of Phuket and the Malacca Coastal Strait of Thailand.
                </Card.Text>
                <Button variant="primary">Read more</Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
